import Link from 'ckeditor5-link/link.js';
import proof from 'src/proof';

proof( 'Calling from a plugin.' );

export default class ClassicCreator {
	constructor( editor ) {
		console.log( 'ClassicCreator.constructor()' );

		this.editor = editor;

		// Checking if cross-package imports work...
		this.editor.features.set( 'link', new Link() );
	}
}